mapper.select:
select id,experiment_code,username,experiment_name,course_name,
experiment_start_time, experiment_end_time from tb_experiment
where id=#{id}

mapper.update:
update tb_experiment
course_name=#{courseName}, experiment_name=#{experimentName}, create_time=#{createTime}, experiment_start_time=#{experimentStartTime}, update_time=now()
where id=#{id}
