mapper.select:
select id,experiment_code,username,experiment_name,course_name,
experiment_start_time, experiment_end_time from tb_experiment
where id=#{id}

mapper.update:
update tb_experiment
course_name=#{courseName}, experiment_name=#{experimentName}, experiment_start_time=#{experimentStartTime}, experiment_end_time=#{experimentEndTime}, update_time=now()
where id=#{id}

mapper.select:
select experiment_code
from tb_experiment
where id=#{id}
